#include "duckboard_r3.h"

bool encoder_update_kb(uint8_t index, bool clockwise) {
    if (!encoder_update_user(index, clockwise)) return false;
    if (index == 0) {
        if (clockwise) {
            tap_code16(KC_MS_WH_UP);
        } else {
            tap_code16(KC_MS_WH_DOWN);
        }
    }
    return true;
}

